import collections
from typing import List

class Solution2(object):
    def numDistinctIslands(self, grid):
        seen = set()
        def explore(r, c, r0, c0):
            if (0 <= r < len(grid) and 0 <= c < len(grid[0]) and
                    grid[r][c] and (r, c) not in seen):
                seen.add((r, c))
                shape.add((r - r0, c - c0))
                explore(r+1, c, r0, c0)
                explore(r-1, c, r0, c0)
                explore(r, c+1, r0, c0)
                explore(r, c-1, r0, c0)

        shapes = set()
        for r in range(len(grid)):
            for c in range(len(grid[0])):
                shape = set()
                explore(r, c, r, c)
                if shape:
                    shapes.add(frozenset(shape))
        return len(shapes)


class SolutionDFS:
    def numDistinctIslands(self, grid: List[List[int]]) -> int:
        if not grid:
            return 0
        islands = set()
        n = len(grid)
        m = len(grid[0])
        cn = 0
        temp_island = set()
        init_i, init_j = 0, 0

        def dfs(i, j):
            if grid[i][j] == 0:
                return
            grid[i][j] = 0
            temp_island.add((i - init_i, j - init_j))
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                x, y = i + dx, j + dy
                if 0 <= x < n and 0 <= y < m and grid[x][y] == 1:
                    dfs(x, y)

        count = 0
        for i in range(n):
            for j in range(m):
                temp_island = set()
                if grid[i][j] == 1:
                    init_i, init_j = i, j
                    dfs(i, j)
                    island = tuple(temp_island)
                    if island not in islands:
                        islands.add(island)
                        count += 1
        return count


class SolutionBFS:
    def numDistinctIslands(self, grid: List[List[int]]) -> int:
        if not grid:
            return 0
        islands = set()
        n = len(grid)
        m = len(grid[0])

        def bsf(i, j):
            cn = 0
            tot = 0
            island = set()
            init_i, init_j = i, j
            q = collections.deque([(i, j)])
            while q:
                x, y = q.popleft()
                tot+=1
                if grid[x][y] == 0:
                    continue
                cn+=1
                grid[x][y] = 0
                island.add((x - init_i, y - init_j))
                for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    k, p = x + dx, y + dy
                    if 0 <= k < n and 0 <= p < m and grid[k][p] == 1:
                        q.append((k, p))
            print(cn, tot)
            return tuple(island)

        count = 0
        for i in range(n):
            for j in range(m):
                if grid[i][j] == 1:
                    island = bsf(i, j)
                    if island not in islands:
                        islands.add(island)
                        count += 1
        return count

